import React, { useEffect } from 'react';
import "./HomePage.scss";
import HeaderSlider from "../../components/Slider/HeaderSlider";
import { useSelector, useDispatch } from 'react-redux';
import { getAllCategories } from '../../store/categorySlice';
import ProductList from "../../components/ProductList/ProductList";
import { fetchAsyncProducts, getAllProducts, getAllProductsStatus } from '../../store/productSlice';
import Loader from "../../components/Loader/Loader";
import { STATUS } from '../../utils/status';

const HomePage = () => {
  const dispatch = useDispatch();
  const categories = useSelector(getAllCategories);

  useEffect(() => {
    dispatch(fetchAsyncProducts(50));
  }, [dispatch]);

  const products = useSelector(getAllProducts);
  const productStatus = useSelector(getAllProductsStatus);

  // randomizing the products in the list
  const tempProducts = [];
  if(products.length > 0){
    for(let i in products){
      let randomIndex = Math.floor(Math.random() * products.length);

      while(tempProducts.includes(products[randomIndex])){
        randomIndex = Math.floor(Math.random() * products.length);
      }
      tempProducts[i] = products[randomIndex];
    }
  }

  const getCategoryName = (category) => {
    if (typeof category === 'string') return category;
    if (typeof category === 'object' && category !== null) return category.name || 'Unknown Category';
    return 'Unknown Category';
  };

  const getCategoryProducts = (categoryIndex) => {
    const category = categories[categoryIndex];
    if (!category) return [];
    const categoryName = getCategoryName(category);
    return products.filter(product => product.category === categoryName);
  };

  const categoryProducts = [
    getCategoryProducts(0),
    getCategoryProducts(1),
    getCategoryProducts(2),
    getCategoryProducts(3)
  ];

  return (
    <main>
      <div className='slider-wrapper'>
      <HeaderSlider />
      </div>
      <div className='main-content bg-whitesmoke'>
        <div className='container'>
          <div className='categories py-5'>
            <div className='categories-item'>
              <div className='title-md'>
                <h3>See our products</h3>
              </div>
              {productStatus === STATUS.LOADING ? <Loader /> : <ProductList products={tempProducts} />}
            </div>

            {categories.slice(0, 4).map((category, index) => (
              <div key={index} className='categories-item'>
                <div className='title-md'>
                  <h3>{getCategoryName(category)}</h3>
                </div>
                {productStatus === STATUS.LOADING ? <Loader /> : <ProductList products={categoryProducts[index]} />}
              </div>
            ))}
          </div>
        </div>
      </div>
    </main>
  )
}

export default HomePage;
