import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import { BASE_URL } from "../utils/apiURL";
import { STATUS } from "../utils/status";

const initialState = {
    categories: [],
    categoriesStatus: STATUS.IDLE,
    categoryProducts: [],
    categoryProductsStatus: STATUS.IDLE
}

const categorySlice = createSlice({
    name: 'category',
    initialState,
    reducers: {},
    extraReducers: (builder) => {
        builder
        .addCase(fetchAsyncCategories.pending, (state, action) => {
            state.categoriesStatus = STATUS.LOADING;
        })

        .addCase(fetchAsyncCategories.fulfilled, (state, action) => {
            console.log('Received categories:', action.payload); // Debug log

            // Check if payload is an array
            if (Array.isArray(action.payload)) {
                state.categories = action.payload.map(category => {
                    if (typeof category === 'string') {
                        return {
                            slug: category.toLowerCase().replace(' ', '-'),
                            name: category,
                            url: `/category/${category.toLowerCase().replace(' ', '-')}`
                        };
                    } else if (typeof category === 'object' && category !== null) {
                        // If category is already an object, use its properties
                        return {
                            slug: category.slug || category.name?.toLowerCase().replace(' ', '-') || '',
                            name: category.name || '',
                            url: category.url || `/category/${category.slug || category.name?.toLowerCase().replace(' ', '-') || ''}`
                        };
                    } else {
                        console.error('Invalid category:', category);
                        return null;
                    }
                }).filter(Boolean); // Remove any null entries
            } else {
                console.error('Received payload is not an array:', action.payload);
                state.categories = [];
            }

            state.categoriesStatus = STATUS.SUCCEEDED;
        })

        .addCase(fetchAsyncCategories.rejected, (state, action) => {
            state.categoriesStatus = STATUS.FAILED;
        })

        // ... rest of the code remains the same
    }
});

