// src/utils/helpers.js

// src/utils/helpers.js

const USD_TO_KES_RATE = 100.26; // Update this rate as needed

export const formatPrice = (price) => {
  const kesPrice = (price * USD_TO_KES_RATE).toFixed(2);
  return `KES ${Number(kesPrice).toLocaleString()}`;
};
